import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { signUp } from '../../store/actions/authActions';

class SignUp extends Component {
    state = {
       email: '',
       password: '',
       firstName: '',
       lastName: ''
    }
    handleChange = (e) => {
        this.setState({
           [e.target.id]: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.signUp(this.state);
   }
   render() {
       const { authError, auth } = this.props;
       const authErrormessage = error.message;
       if(auth.uid) return <Redirect to='/' />
       return (
           <div className="container">
           <form onSubmit={this.handleSubmit} className="white">
               <h5 className="grey-text text-darken-3">Sign Up</h5>
               <div className="input-field">
                   <label htmlFor="email">Email</label>
                   <input type="email" id="email" onChange={this.handleChange} />
               </div>
               <div className="input-field">
                   <label htmlFor="password">password</label>
                   <input type="password" id="password" onChange={this.handleChange } />
               </div>
               <div className="input-field">
                   <label htmlFor="firstName">First Name</label>
                   <input type="text" id="firstName" onChange={this.handleChange} />
               </div>
               <div className="input-field">
                   <label htmlFor="lastName">Last Name</label>
                   <input type="text" id="lastName" onChange={this.handleChange} />
               </div>
               <div className="input-field">
                   <button className="btn teal accent-3 z-depth-0">Sign Up</button>
                   <div className='center red-text'>
                     { authErrormessage ? <p>{authErrormessage}</p> : null }
                   </div>
               </div>
           </form>
               
           </div>
       )
   }
}

const mapStateToProps = (state) => {
    return {
      authError: state.auth.authError,
      auth: state.firebase.auth
      
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        signUp: (newUser) => dispatch(signUp(newUser))//attaching 
        //to our props a method for signing up. it returns a function
        //that takea a new user as a parameter and calls a dispatch
        //with action creator signUp, that we have mported at the top
        //this action creator takes in newUser that we are receiving 
        //in authAction/ signUp
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
