const Uploaded = (props) => {
    const {project} = props;
    console.log(project)
    return (
        project ? (
              <>
              <div id='relFile' onClick={handleShowFile}>
              <img src={project.url} alt='relatedFile' style="display:none"></img>
                {project.relatedFile}
              </div>  
             </>
      ) : ('no details')
    );
}

const handleShowFile = () => {
    document.getElementById('relFile').style.display = 'block';
}


const mapStateToProps = (state, ownProps) => {
    const projects = state.firestore.ordered.projects;
    const project = ownProps.project;
     console.log(project)
    return {
        projects: projects,
        auth: state.firebase.auth,
    }//this object is what we would like to attach to our props
}
    
    export default compose(//compose connected several HOCs, just
        //like store enhancers in Index.js
        connect(mapStateToProps),
        firestoreConnect([
            { collection: 'projects' },
    
        ])//triggers firestoreReducer to sync changes with firebase. takes in an 
