const Uploaded = (props, ownProps) => {
  const {project, projects} = props;
    console.log(project)
    return (
        <div className="project-list section">
          { projects && projects.map(project => {
            return(
              <Link to={'/uploaded/' + project.id}  key={project.id}>
                <img alt='relatedFile' src={project.url}></img>
              </Link>
            )
          })}
        </div>
        
    )


};




//Uploaded();

const mapStateToProps = (state, ownProps) => {
    const projects = state.firestore.ordered.projects;
    console.log(state)
    return {
        projects: projects,
        auth: state.firebase.auth,
    }//this object is what we would like to attach to our props
}
    
    export default compose(//compose connected several HOCs, just
        //like store enhancers in Index.js
        connect(mapStateToProps),
        firestoreConnect([
            { collection: 'projects' },
    
        ])//triggers firestoreReducer to sync changes with firebase. takes in an 
        //array as a parameter. passing an object of Project collection
    )(Uploaded);
