import { CREATE_PROJECT, CREATE_PROJECT_ERROR} from '../../components/action-types';

const initState = {}

const projectReducer = (state = initState, action) => {
   switch (action.type) {
     case CREATE_PROJECT:
       console.log('created project');
       return state;
     case CREATE_PROJECT_ERROR :
       console.log('create project error');
       return state; 
       case "EDIT_PROJECT":
      return {
        ...state,
        text: state.projects[action.payload],
        selected: action.payload
      };
    //  case UPLOAD_RELATED:
    //    console.log('uploaded file created');
    //     return state;
    //  case UPLOAD_RELATED_ERROR:
    //    console.log('uploaded file error') 
    //    return state; 
    default:   
       return state; 
   }
};

export default projectReducer;
