import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createProject} from '../../store/actions/projectActions';
import { Redirect } from 'react-router-dom';
import firebase from 'firebase/app';
import 'firebase/storage';




class CreateProject extends Component {
    state = {
        title: '',
        content: '',
        relatedFile: null,
        url: null
    
    }
    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault()
        this.props.createProject(this.state) 
        console.log(this.state)  
        this.props.history.push('/');
        
       
    }
    handleClick =  async (e) => {
        e.preventDefault()
        const firestore = getFirebase().firestore();
        const file = e.target;
        const storageRef = firebase.storage().ref();
        const fileRef = storageRef.child(file.name);

        firestore.collection('projects')
             await fileRef.put(file)
             fileRef.getDownloadURL()
            .then((url) => {
                const relatedFile = file.name
                this.setState({
                    relatedFile,
                    url
                        })
            console.log(relatedFile)
    })
}

    render() {
        const { auth } = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Case create</h5>
                    <div className="input-field">
                        <label htmlFor="title">Category</label>
                        <input type="text" id="title" onChange={this.handleChange} />
                    </div>
                    <div className="input-field">
                        <label htmlFor="content">Case Content</label>
                        <textarea id="content" className="materialize-textarea" onChange={this.handleChange}></textarea>
                    </div>

                    <div onChange={this.handleClick} className="file-field input-field">
                        <div className="btn teal accent-3 z-depth-1 col s2">
                            Upload file
                        </div>
                            <input type="file" />
                        <div className="file-path-wrapper">
                            <input className="file-path validate" type="text" placeholder="Browse files" />
                        </div>
                    </div>
                    <div className="input-field row">
                        <button className="btn teal accent-3 z-depth-1 col s1 right">Create</button>
                    </div>

                </form>

            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        auth: state.firebase.auth
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createProject: (project) => dispatch(createProject(project)),
        uploadFile: (project) => dispatch(uploadFile(project)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateProject);
