import React, { Component } from 'react';
import Notifications from './Notifications';
import ProjectList from '../projects/ProjectList';
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';

class Dashboard extends Component {
    render() {
        //console.log(this.props);
        const { projects } = this.props;
        return (
            <div className="dashboard container">
                <div className="row">
                    <div className="col s12 m6">
                      <ProjectList projects={projects}/>
                    </div>
                    <div className="col s12 m5 offset-m1">
                      <Notifications />
                    </div>
                   
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    console.log(state)
    return {
        projects: state.firestore.ordered.projects || state.project.projects// state is passed form rootReducer,
        //project is a property of the state in rootReducer
        //projects is a property of the project object in projectReducer, initState
    };
};

export default compose(//compose connected several HOCs, just
    //like store enhancers in Index.js
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'projects' }
    ])//triggers firestoreReducer to sync changes with firebase. takes in an 
    //array as a parameter. passing an object of Project collection
)(Dashboard);
