const initState ={
  authError: null,
}

const authReducer = (state = initState, action) => {
  switch(action.type) {
    case 'LOGIN_ERROR':
      console.log('login error');
      return {
        ...state,
        authError: 'Login failed'
      }
    case 'LOGIN_SUCCESS':
      console.log('login sucess');
      return {
        authError: null
      }
    case 'SIGNOUT_SUCCESS':
        console.log('logout sucess');
        return state;
    case 'SIGNUP_SUCCESS':
        console.log('signup sucess', state)
        return {
          ...state,
          authError: null
        } 
    case 'SIGNUP_ERROR':
      console.log('signup error')
        return {
          ...state,
          authError: null
        }   
    default:
      return state
  }
};

export default authReducer;
