import React, { Component } from 'React';

class Timer extends Component {
    constructor(props){
        super(props)
        this.state = {
            count: '04.00.00'
        }

    }
    render() {
        const count = this.state;
        return (
            <div>
                <h1>Time left: {count}</h1>
            </div>
        )
    }

    componentDidMount() {
        this.setState({
            count: this.state.count - 1
        })
    }
}

export default Timer;
