import React, { Component } from 'react'; // rsf for functional component from snippet
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
import { Redirect } from 'react-router-dom';
import moment from 'moment';
import Uploaded from '../Uploaded';
import firebase from 'firebase/app';
import 'firebase/storage';
import Timer from '../Timer';
import { createProject, addInfo } from '../../store/actions/projectActions';





class ProjectDetails extends Component {    

    handleChange = (e) => {
        const {project} = this.props;
        this.setState({
            [e.target.id]: [
                ...project.additionalNotes, 
                e.target.value
            ]
        })
    }


    // handleClick = async (e) => {
    //     e.preventDefault()
    //     const {project} = this.props;
    //     const file = e.target.files[0];
    //     console.log(file)
    //     const storageRef = firebase.storage().ref();
    //     const fileRef = storageRef.child(file.name);

    //     await fileRef.put(file)
    //     fileRef.getDownloadURL()
    //      .then((url) => {
    //       const relatedFile = file.name;
    //       console.log(file.name)
    //       this.setState({
    //             relatedFile:  [...project.relatedFile, relatedFile],
    //             url: [...project.url, url]
    //           })

    //       })
    // }

    handleClick = (e) => {
        e.preventDefault()
        const uploader = document.getElementById('uploader');
        const onComplete = document.getElementById('onComplete');
        const {project} = this.props;
        const file = e.target.files[0];
        console.log(file)
        const storageRef = firebase.storage().ref();
        const fileRef = storageRef.child(file.name);

        const task = fileRef.put(file)
        task.on('state changed',
          
        function progress(snapshot) {
          const percentage = (snapshot.bytesTransferred / 
          snapshot.totalBytes) * 100;
          uploader.value = percentage;
        },

        function error(error) {
          console.log(error)
        },

        function complete() {
         return ('file has been successfully uploaded');
        },
        
        )
        fileRef.getDownloadURL()
         .then((url) => {
          const relatedFile = file.name;
          console.log(file.name)
          this.setState({
                relatedFile:  [...project.relatedFile, relatedFile],
                url: [...project.url, url]
              })

          })
    }
  
    handleSubmit = (e) => {
        const id = this.props.match.params.id;
        e.preventDefault();       
            this.props.addInfo({
                ...this.props.project,
                ...this.state,
            }, id);
        e.target.reset();
        
         this.props.history.push('/'); 
         
       };

   

    render() {
        console.log('state', this.props.match.params.id)
        const { project, auth } = this.props;
        //console.log('prd: ', project && project.url[0]);
        console.log('props',this.props)
        if (!auth.uid) return <Redirect to='/signin' />
        if (project) {
            return (
                <div className="container section project-details">
                    <div className="card z-depth-1">
                        <div className="card-content row">
                            <span className="card-title">{project.title} </span>
                            <p>{project.content}</p>
                            <div>
                                <div> Additional notes: 
                                 { project.additionalNotes && project.additionalNotes.map((note) => {
                                     return(
                                       <ul key={note.id}>
                                         <li key={note.id}>{note}</li>
                                         <div>added at: {moment(project.updatedAt.toDate()).calendar()}</div>
                                       </ul>
                                     )
                                     
                                 })}
                                
                               </div>
                               
                            </div>
                              Related files:
                            <Uploaded project={project} />
                            <form onSubmit={this.handleSubmit} className="white">
                               <div className="input-field">
                                  <label htmlFor="content">additional notes</label>
                                  <textarea id="additionalNotes" className="materialize-textarea" onChange={this.handleChange}></textarea>
                               </div>
                               <div onChange={this.handleClick} className="file-field input-field">
                                    <div className="btn z-depth-1 col s2">
                                        Add files
                                    </div>
                                    <input type="file" />
                                    <div className="file-path-wrapper">
                                        <input className="file-path validate" type="text" id="inputs" placeholder="Browse files" />
                                    </div>
                                </div>
                                <progress value='0' max='100' id='uploader'>0%</progress>
                                <button className="btn z-depth-1 right"> Submit </button>
                            </form>
                            <Timer />
                            <div>Posted by {project.authorFirstName} {project.authorLastName}</div>
                            <div>{moment(project.createdAt.toDate()).calendar()}</div>
                        </div>
                    </div>

                </div>
            )
        } else {
            return (
                <div className='container center'>
                    <p>Loading project...</p>
                </div>
            )
        }

    }


}


const mapStateToProps = (state, ownProps) => {
    const id = ownProps.match.params.id//route parameters id with own props of 
    //given component
    const projects = state.firestore.data.projects;
    const project = projects ? projects[id] : null;
    const relatedFile = projects ? project.relatedFile : null;
    const url = project ? project.url : null;
   
    
    return {
        project: project,
        auth: state.firebase.auth,
        relatedFile: relatedFile,
        url: url,
    
        //uploadedFile: uploadedFile,

    }//this object is what we would like to attach to our props
}

const mapDispatchToProps = (dispatch) => {
    return {
        createProject: (project) => dispatch(createProject(project)),
        addInfo: (project, id) => dispatch(addInfo(project, id))
    }
};

export default compose(
    connect(mapStateToProps, mapDispatchToProps),
    firestoreConnect([
        { collection: 'projects' }
    ])
)(ProjectDetails);
