import React from 'react'; // rsf for functional component from snippet
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';

const ProjectDetails = (props) => {
    const id = props.match.params.id;
    return (
        <div className="container section project-details">
           <div className="card z-depth-0">
               <div className="card-content">
                   <span className="card-title">Project title - {id}</span>
                   <p>Lorem  simply dummy text of the printing
                    and typesetting industry. Lorem Ipsum has been
                     the industry's standard dummy text ever since 
                     the 1500s, when an unknown printer took a galle
                     ype and scrambled it to make a type specimen b</p>
               </div>
               <div className="card-action grey-lighten-4 grey-text">
                   <div>Posted by Elena</div>
                   <div>24th of April 16.00</div>
               </div>
           </div> 
        </div>
    );
}

const mapStateToProps = (state, ownProps) => {
    const id = ownProps.match.params.id//route parameters id with own props of 
    //given component
    const projects = state.firestore.data.projects;
    const project = projects ? projects[id] : null;
    return {
      project: project
    }//this object is what we would like to attach to our props
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        {collection: 'projects'}
    ])
)(ProjectDetails);
