import React, { Component } from 'react';
import Notifications from './Notifications';
import ProjectList from '../projects/ProjectList';
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
import { Redirect } from 'react-router-dom';


class Uploaded extends Component {
    render() {
        //console.log(this.props);
        const { projects, auth, notifications } = this.props;
        if(!auth.uid) return <Redirect to='/signin' /> 
        return (
            <div className="dashboard container">
                <div className="row">
                    <div className="col s6 m6">
                      {projects.fileName}
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    //console.log(state);
    return {
        projects: state.firestore.ordered.projects,// state is passed form rootReducer,
        //project is a property of the state in rootReducer
        //projects is a property of the project object in
        // projectReducer, initState
        auth: state.firebase.auth,
        uploaded: state.firestore.ordered.url
    };
};

export default compose(//compose connected several HOCs, just
    //like store enhancers in Index.js
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'projects', limit: 4, orderBy: ['createdAt', 'desc'] },
        { collection: 'notifications', limit: 2, orderBy: ['time', 'desc'] }
    ])//triggers firestoreReducer to sync changes with firebase. takes in an 
    //array as a parameter. passing an object of Project collection
)(Dashboard);
