export const addInfo = (project, projectId) => {
    return(dispatch, {getFirebase, getFirestore}) => {
        console.log('getFirebase projectID:', projectId)
        const firestore = getFirebase().firestore();
        project && firestore.collection('projects').doc(projectId).set({
            ...project,
            relatedFile: project.relatedFile,
            url: project.url,
            updatedAt: new Date(),
        })
        .then(() => {
            dispatch({type: 'ADD_INFO', project }); 
        })
    }
}

export const findProject = (project, {getFirebase}) => {
    return (dispatch) => {
        const firestore = getFirebase().firestore();
        firestore.collection('projects').get().then((snapshot) => {
console.log(snapshot.doc)
        })
    }
}
