//import firebase from 'firebase/app';
import 'firebase/storage';

// import { CREATE_PROJECT, CREATE_PROJECT_ERROR } from '../../components/action-types';

export const createProject = (project) => {
    return (dispatch, getState, {getFirebase, getFirestore}) => {
        const firestore = getFirebase().firestore();
        const profile = getState().firebase.profile;
        const authorId = getState().firebase.auth.uid;
        const projectId = Math.floor(Math.random() * 100000);
        
        
        firestore.collection('projects').add({
            ...project,
            authorFirstName: profile.firstName,
            authorLastName: profile.lastName,
            authorId: authorId,
            createdAt: new Date(),
            projectId: projectId,
        }).then(() => {
            dispatch({type: 'CREATE_PROJECT', project });
        }).catch(err => {
            dispatch({type: 'CREATE_PROJECT_ERROR', err});
        });
        
        
    }
};// we are passing this.state to this project action. Inside here we are
// returning a function. We used thunk to do this and we get access to a 
//dispatch and store enhancers. Here we are pausing dispatch while we are
// asyncroniusly adding a document to firebase.



export const addText = value => ({
    type: "ADD_TEXT",
    payload: value
  });
