import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createProject } from '../../store/actions/projectActions';
import { Redirect } from 'react-router-dom';
import firebase from 'firebase/app';


class CreateProject extends Component {
    state = {
       title: '',
       content: ''
    }
    handleChange = (e) => {
        this.setState({
           [e.target.id]: e.target.value
        })
    }
    handleSubmit = (e) => {
      e.preventDefault();
      this.props.createProject(this.state)
      this.props.history.push('/');
   }

   handleUploadFile = (e) => {
       const file = e.target.files[0];
       const storageRef = firebase.storage().ref('file_uploads/' + file.name);
       const task = storageRef.put(file);
       task.on('state_changed', 
         function progress(snapshot) {
           const percentage = (snapshot.bytesTransferred /
            snapshot.totalBytes) * 100;
            
         },
         function error(err) {

         },
         function complete() {

         }
       )
   }
   render() {
       const { auth } = this.props;
       if(!auth.uid) return <Redirect to='/signin' /> 
       return (
           <div className="container">
           <form className="white">
               <h5 className="grey-text text-darken-3">Case create</h5>
               <div className="input-field">
                   <label htmlFor="title">Category</label>
                   <input type="text" id="title" onChange={this.handleChange} />
               </div>
               <div className="input-field">
                   <label htmlFor="content">Case Content</label>
                   <textarea id="content" className="materialize-textarea"  onChange={this.handleChange}></textarea>
               </div>
               <div className="input-field row">
                   <button onSubmit={this.handleSubmit} className="btn teal accent-3 z-depth-1 col s1 right">Create</button>
               <div className="file-field input-field">
                   <div onChange={this.handleUploadFile} className="btn teal accent-3 z-depth-1 col s2">Upload file</div>
                     <input type = "file" />
                     <div class = "file-path-wrapper">
                     <input class = "file-path validate" type = "text"
                        placeholder = "Brouse files" />
                  </div>
               </div>
               </div>
           </form>
               
           </div>
       )
   }
}

const mapStateToProps = (state) => {
  return {
      auth: state.firebase.auth
  }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createProject: (project) => dispatch(createProject(project))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateProject);
