import authReducer from './authReducer';
import projectReducer from './projectReducer';
import { combineReducers } from 'redux';
import { firestoreReducer } from 'redux-firestore';
import { firebaseReducer } from 'react-redux-firebase';

const rootReducer = combineReducers({
    auth: authReducer,
    //project: projectReducer,
    firebase: firebaseReducer,
    firestore: firestoreReducer
});

// firebase reducer is syncing users
//state(auth service) in firebase (i.e. logged in/logged out...) with
// state of the app


// firestore reducer on the 
//background is syncing firestore data with
//the store state object 
//firestore contains firebase data and defines
//which one is active
//we need to connect component with a single
// collection in firestore so that reducer 
//knows what data to sync. Mooving to Dashboard

export default rootReducer;
