const initState ={
  projects: [
    {id: '1', title: 'new owner', content: 'customer is willing to resign contract'},
    {id: '1', title: 'new owner', content: 'customer is willing to resign contract'},
    {id: '1', title: 'new owner', content: 'customer is willing to resign contract'}
  ]
}

const authReducer = (state = initState, action) => {
   switch (action.type) {
     case 'ADD_PROJECT':
       console.log('created project');
       return state;
     case 'CREATE_PROJECT_ERROR':
       console.log('create project error', action.err);
       return state;
     default:  
       return state; 
   }
}

export default authReducer;
