import React from 'react'; // rsf for functional component from snippet
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
import { Redirect } from 'react-router-dom';
import moment from 'moment';
import ProjectSummary from './ProjectSummary';


const ProjectDetails = (props) => {
    const { project, auth } = props;
    if (!auth.uid) return <Redirect to='/signin' />
    if (project) {
        return (
            <div className="container section project-details">
                {/* <div className="card z-depth-1">
                    <div className="card-content row">
                        <span className="card-title">{project.title} </span>
                        <p>{project.content}</p>
                        <a className='btn' href='/uploaded'>Related documents</a>
                       
                        <div className="card grey-lighten-4 grey-text z-depth-2">
                            <div>Posted by {project.authorFirstName} {project.authorLastName}</div>
                            <div>{moment(project.createdAt.toDate()).calendar()}</div>
                        </div>
                    </div>
                </div> */}
                <ProjectSummary />
            </div>
        )
    } else {
        return (
            <div className='container center'>
                <p>Loading project...</p>
            </div>
        )
    }

}

const mapStateToProps = (state, ownProps) => {
    const id = ownProps.match.params.id//route parameters id with own props of 
    //given component
    const projects = state.firestore.data.projects;
    const project = projects ? projects[id] : null;
    return {
        project: project,
        auth: state.firebase.auth
    }//this object is what we would like to attach to our props
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'projects' }
    ])
)(ProjectDetails);
